CREATE VIEW "V_MRP_FIELDS" AS SELECT "im" ."part" ,"im" ."location" ,"im" ."product_line" ,"im" ."qty_order" ,"im" ."qty_onhand" ,"im" ."qty_reorder" ,"im" ."code_sort" ,"im" ."time_material_lead" "PURCHASE_LEAD" ,"im" ."flag_drop" ,"im" ."flag_inactive" ,"i2" ."qty_maximum" ,"i2" ."code_source" ,"i3" ."temp_purch" ,"i3" ."mfg_lead" ,"i3" ."mfg_qty_minimum" ,"i3" ."mfg_qty_multiple" FROM "inventory_mstr" "im" LEFT JOIN "inventory_mst2" "i2" ON "im" ."part" = "i2" ."part" AND "im" ."location" = "i2" ."location" LEFT JOIN "inventory_mst3" "i3" ON "i2" ."part" = "i3" ."part" AND "i2" ."location" = "i3" ."location"
